import React, { Component } from "react";
import EmployeeService from '../services/EmployeeService';
import Cookies from 'universal-cookie';


export default class LoginComponent extends React.Component {
    constructor(props) {
        super(props);
        this.state = { emailId: "", password: "" };
        this.onLoginClick = this.onLoginClick.bind(this);
    }
    onLoginClick = (e) => {
        e.preventDefault();
        console.log('email', this.state.emailId)
        console.log('password', this.state.password)
        let employee = { emailId: this.state.emailId, password: this.state.password }
        EmployeeService.loginEmployee(employee).then(res => {
            if (res.data === true) {
                const cookies = new Cookies();
                cookies.set('loginStatus', 'true');
                this.props.history.push('/employees');
            } else {
                alert("You have entered an invalid email or password");
            }
        });
    }
    render() {
        return <div className={"mainContainer"}>
        <div className={"titleContainer"}>
            <div>Login</div>
        </div>
        <br />
        <div className={"inputContainer"}>
            <input
                value={email}
                placeholder="Enter your email here"
                onChange={this.handleEmailChange}
                className={"inputBox"}
            />
            <label className="errorLabel">{emailError}</label>
        </div>
        <br />
        <div className={"inputContainer"}>
            <input
                value={password}
                placeholder="Enter your password here"
                onChange={this.handlePasswordChange}
                className={"inputBox"}
            />
            <label className="errorLabel">{passwordError}</label>
        </div>
        <br />
        <div className={"inputContainer"}>
            <input
                className={"inputButton"}
                type="button"
                onClick={this.onButtonClick}
                value={"Log in"}
            />
        </div>
    </div>
    }

}
