import React, { Component } from "react";
import EmployeeService from '../services/EmployeeService';
import Cookies from 'universal-cookie';
import Styles from './employee.module.css'


export default class LoginComponent extends React.Component {
    constructor(props) {
        super(props);
        this.state = { emailId: "", password: "",emailError:"",passWordError:"" };
        this.onLoginClick = this.onLoginClick.bind(this);
    }
    onLoginClick = (e) => {
        e.preventDefault();
        this.setState({emailError:""});
        this.setState({passWordError:""});
        console.log('email', this.state.emailId)
        console.log('password', this.state.password)
        if ("" === this.state.emailId) {
            this.setState({emailError:"Please enter your email"})
            return
        }
        console.log("1 passed")
        if (!/^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/.test(this.state.emailId)) {
            this.setState({emailError:"Please enter a valid email"})
            return
        }
        console.log("2 passed")
        if ("" === this.state.password) {
            console.log()
            this.setState({passWordError:"Please enter a password"})
            return
        }
        console.log("3 passed")
        if (this.state.password.length < 7) {
            this.setState({passWordError:"The password must be 8 characters or longer"})
            return
        }
        console.log("4 passed")
        
        let employee = { emailId: this.state.emailId, password: this.state.password }
        EmployeeService.loginEmployee(employee).then(res => {
            if (res.data === true) {
                const cookies = new Cookies();
                cookies.set('loginStatus', 'true');
                this.props.history.push('/employees');
            } else {
                alert("You have entered an invalid email or password");
            }
        });
    }
    render() {
        return <div className={Styles.mainContainer}>
        <div className={Styles.titleContainer}>
            <div>Login</div>
        </div>
        <br />
        <div className={Styles.inputContainer}>
            <input
                value={this.state.emailId}
                placeholder="Enter your email here"
                onChange={(event) => {
                    this.setState({ emailId: event.target.value });
                    console.log(this.state.emailId);
                }}
                className={Styles.inputBox}
            />
            <label className={Styles.errorLabel}>{this.state.emailError}</label>
        </div>
        <br />
        <div className={Styles.inputContainer}>
            <input
                value={this.state.password}
                placeholder="Enter your password here"
                onChange={(event) => {
                    this.setState({ password: event.target.value });
                    console.log(this.state.password);
                }}
                className={Styles.inputBox}
            />
            <label className={Styles.errorLabel}>{this.state.passwordError}</label>
        </div>
        <br />
        <div className={Styles.inputContainer}>
            <input
                className={Styles.inputButton}
                type="button"
                onClick={this.onLoginClick}
                value={"Log in"}
            />
        </div>
    </div>
    }

}
