import React, { Component } from "react";
import EmployeeService from '../services/EmployeeService';
import Cookies from 'universal-cookie';


export default class LoginComponent extends React.Component {
    constructor(props) {
        super(props);
        this.state = { emailId: "", password: "" };
        this.onLoginClick = this.onLoginClick.bind(this);
    }
    onLoginClick = (e) => {
        e.preventDefault();
        console.log('email', this.state.emailId)
        console.log('password', this.state.password)
        let employee = { emailId: this.state.emailId, password: this.state.password }
        EmployeeService.loginEmployee(employee).then(res => {
            if (res.data === true) {
                const cookies = new Cookies();
                cookies.set('loginStatus', 'true');
                this.props.history.push('/employees');
            } else {
                alert("You have entered an invalid email or password");
            }
        });
    }
    render() {
        return <div className="col-lg-9 container ">
            <br /><br /><br />
            <h4 className="m-1 p-2">Login</h4>
            <div className="form-group form-row">
                <div className="col-lg-5">
                <label >Email:</label>
                <input type="text" className="form-control" value={this.state.emailId}
                    onChange={(event) => { this.setState({ emailId: event.target.value }); console.log(this.state.emailId); }} />
                </div>
            </div>
            <div className="col-lg-5">
            <div className="form-group form-row">
                <label className="col-lg-4">Password:</label>
                <input
                    type="password"
                    className="form-control"
                    value={this.state.password}
                    onChange={(event) => {
                        this.setState({ password: event.target.value });
                        console.log(this.state.password);
                    }}
                />
            </div>
            </div>
            
            <br></br>
            <div>
                <button className="btn btn-primary" onClick={this.onLoginClick}>Login</button>
            </div>
        </div>;
    }

}
