            setPasswordError("The password must be 8 characters or longer")
            return
        }

        console.log('email', this.state.emailId)
        console.log('password', this.state.password)
        let employee = { emailId: this.state.emailId, password: this.state.password }
        EmployeeService.loginEmployee(employee).then(res => {
            if (res.data === true) {
                const cookies = new Cookies();
                cookies.set('loginStatus', 'true');
                this.props.history.push('/employees');
            } else {
                alert("You have entered an invalid email or password");
            }
        });
    }
    render() {
        return <div className={Styles.mainContainer}>
        <div className={Styles.titleContainer}>
            <div>Login</div>
        </div>
        <br />
        <div className={Styles.inputContainer}>
            <input
                value={this.state.emailId}
                placeholder="Enter your email here"
                onChange={(event) => {
                    this.setState({ emailId: event.target.value });
                    console.log(this.state.emailId);
                }}
                className={Styles.inputBox}
            />
            <label className={Styles.errorLabel}>email Error</label>
        </div>
        <br />
        <div className={Styles.inputContainer}>
            <input
                value={this.state.password}
                placeholder="Enter your password here"
                onChange={(event) => {
                    this.setState({ password: event.target.value });
                    console.log(this.state.password);
                }}
                className={Styles.inputBox}
            />
            <label className={Styles.errorLabel}>passwordError</label>
        </div>
        <br />
        <div className={Styles.inputContainer}>
            <input
                className={Styles.inputButton}
                type="button"
                onClick={this.onLoginClick}
                value={"Log in"}
            />
        </div>
    </div>
    }

}
