import React from 'react';
import './App.css';
import {BrowserRouter as Router, Route, Switch,Redirect} from 'react-router-dom'
import ListEmployeeComponent from './components/ListEmployeeComponent';
import HeaderComponent from './components/HeaderComponent';
import CreateEmployeeComponent from './components/CreateEmployeeComponent';
import ViewEmployeeComponent from './components/ViewEmployeeComponent';
import LoginComponent from './components/LoginComponent';
import Cookies from 'universal-cookie';
import PrivateRoutes from './components/PrivateRoutes';

function App() {
  const cookies = new Cookies();
  const isAuthenticated = cookies.get('loginStatus');

  return (
    <div>
      <div>
          <HeaderComponent />
          </div>
        <Router>
                <div className="container">
                    <Switch>
                         
                          {/* <Route path = "/employees" component = {ListEmployeeComponent}></Route>
                          <Route path = "/add-employee/:id" component = {CreateEmployeeComponent}></Route>
                          <Route path = "/view-employee/:id" component = {ViewEmployeeComponent}></Route> */}
                          <Route path = "/" exact component = {LoginComponent}></Route>
                          <PrivateRoute exact path="/employees" component={ListEmployeeComponent} isAuthenticated={isAuthenticated} />
                          <PrivateRoute exact path="/add-employee/:id" component={CreateEmployeeComponent} isAuthenticated={isAuthenticated} />
                          <PrivateRoute exact path="/view-employee/:id" component={ViewEmployeeComponent} isAuthenticated={true} />
                    </Switch>
                </div>
        </Router>
    </div>
    
  );
}
function PrivateRoute({ component: Component, isAuthenticated, ...rest }) {
  return (
    <Route {...rest} render={(props) => (
      isAuthenticated ? <Component {...props} />
    : <Redirect to={{ pathname: '/', state: { from: props.location } }} />
    )} />
  );
    }

export default App;
